package edu.uoc.elc.lti;

import edu.uoc.elc.lti.exception.InvalidTokenException;
import org.junit.Assert;
import org.junit.Before;
import org.junit.Test;

/**
 * @author Xavi Aracil <xaracil@uoc.edu>
 */
public class ToolProviderTest {
	private ToolProvider sut;

	@Before
	public void setUp() {
		this.sut = new ToolProvider(
						"Universitat Oberta de Catalunya",
						"Universitat Oberta de Catalunya",
						"https://lti-ri.imsglobal.org/platforms/68/platform_keys/60.json",
						"https://lti-ri.imsglobal.org/platforms/68/access_tokens",
						"",
						""
		);
	}

	@Test
	public void decode() throws InvalidTokenException {
		String token = "eyJhbGciOiJSUzI1NiIsImtpZCI6InBVYUFkb2VmQ2Q1VGctVEM4MDdtalJlSGpTM2VjOG5zWTktbnJwV0RRUzAifQ..S3If5Gz83DW-ZvSqqRnq-ga8uYj-kcOQ2JxuJtqjjERa3x6Z0KB_Sg9LIzgDCg2NqmuLSjP0VT6igbAUV2MekB1nGvjtMkJFxVczBe6BdqlZcDuoWIrxRlRUZ-03KPfLN7ErT4tJc-UksOgeV5m_3o3vIeoaAaJA0Vp8CfQgof54UNMmNEL44a-uOnFOSDptqkRuRRlHm-5UJ2vf2ZzFIyyOil0zJ_SOihpLTf56hAw9DpFWlN2uq0RlKXIPa1HCUrpHgstuez9r7shx1OU_gO9nNHu97x06PamCsPXo5h5wKmaC_MQZqnOosOzdvHq-Ft2hwFEIKIfswivgVmQfOw";
		sut.decode(token);
	}

	@Test(expected = InvalidTokenException.class)
	public void decodeFailWithInvalidToken() {
		String token = "invalid token";

		sut.decode(token);
		Assert.fail();
	}

	@Test
	public void validate() {
		String token = "eyJhbGciOiJSUzI1NiIsImtpZCI6InBVYUFkb2VmQ2Q1VGctVEM4MDdtalJlSGpTM2VjOG5zWTktbnJwV0RRUzAifQ..S3If5Gz83DW-ZvSqqRnq-ga8uYj-kcOQ2JxuJtqjjERa3x6Z0KB_Sg9LIzgDCg2NqmuLSjP0VT6igbAUV2MekB1nGvjtMkJFxVczBe6BdqlZcDuoWIrxRlRUZ-03KPfLN7ErT4tJc-UksOgeV5m_3o3vIeoaAaJA0Vp8CfQgof54UNMmNEL44a-uOnFOSDptqkRuRRlHm-5UJ2vf2ZzFIyyOil0zJ_SOihpLTf56hAw9DpFWlN2uq0RlKXIPa1HCUrpHgstuez9r7shx1OU_gO9nNHu97x06PamCsPXo5h5wKmaC_MQZqnOosOzdvHq-Ft2hwFEIKIfswivgVmQfOw";
		boolean result = sut.validate(token, false);
		Assert.assertTrue(result);
		Assert.assertNull(sut.getReason());
	}

	@Test
	public void validateWithInvalidTokenMustReturnFalse() {
		String token = "invalid token";
		boolean result = sut.validate(token);
		Assert.assertFalse(result);
		Assert.assertNotNull(sut.getReason());
	}

}
